result:
    .purchase_content{
    display: flex;
    justify-content: center;
}
.purchase_content .aside{
    width: 20%;
    margin: 1%;
}
.aside h3{
    font-size: 20px;
    padding: 10px;
}
.aside h3 img{
    width: 30px;
    height: 30px;
    margin-right: 10px;
}
.aside .foodlist li{
    margin-bottom: 10px;
}
.aside .foodlist a{
    display: block;
    text-decoration: none;
    color: #3D1101;
    padding: 10px;
    border-bottom: 1px solid #3D1101;
    transition: all 0.3s;
}
.aside .foodlist a:hover{
    background: #3D1101;
    color: #fff;
}
.purchase_content .section{
    width: 75%;
    margin: 1%;
}
.section .topbar{
    padding:10px;
}
.section .topbar input{
    width: 60%;
    padding: 5px;
}
.section .topbar i{
    color: #fff;
    background: #3D1101;
    margin-left: 1px;
    padding: 7px;
    position: relative;
    top:15px;
    background: url('../assets/images/搜索.svg') no-repeat;
    background-size: 100%;
    display: inline-block;
    width: 30px;
    height: 30px;
    cursor: pointer;
}
.section .topbar ul{
  float: right;
}
.section .topbar ul li{
    float: left;
    border-right:1px solid #3D1101;
    padding: 5px 10px;
}
.section .topbar ul li a{
    color: #3D1101;
    text-decoration: none;
}

.section .topbar .car{
    background: url('../assets/images/shopping-cart-solid.svg') no-repeat;
    width: 30px;
    height: 30px;
    border: #fff;
    margin-left: 10px;
    vertical-align: baseline;
    cursor: pointer;
    padding: 0;
    position: relative;
}
.topbar .car .shop_num{
    font-size: 12px;
    text-align: center;
    color: #fff;
    width: 14px;
    height: 14px;
    padding: 1px;
    border-radius:50px; 
    background: #F56C23;
    position: absolute;
    top:-2px;
    right: -5px;
}
.section .order_section{
    display: flex;
}
.order_list {
  display: flex;
  width: 100%;
  flex-wrap: wrap;
}
.order_list li{
    position: relative;
    width: 30.3333%;
    margin: 2% 0.5%;
    padding: 2% 1%;
    text-align: center;
    box-shadow: 0 0 1px 2px #EEEEEE;
}
@media(max-width:768px){
    /*重直排列*/
    .wrap .purchase_content{
        flex-direction: column;
    }
    /*變更寬度*/
    .purchase_content  .aside,.purchase_content .section{       
        width: 100%;
        margin: 0%;
    }
    .aside .foodlist li{
        text-align: center;
        float: left;
        margin: 10px;
        width: 22%;
    }
    .order_list{
        justify-content: space-around;
    }
    .order_list li{
        width: 46%;
    }
}
@media(max-width:767px){
    .purchase_content .aside{
        position: relative;
    }
    .aside .openlist{
        display: block;
        float: left;
        margin: 10px;
    }
    .order_list li{
        width: 97%;
    }
    .aside h3{
        float: left;
        margin-top: 10px;
    }
    .aside h3 img{
        display: none;
    }
    .aside .foodlist{
        position: absolute;
        top:62px;
        width: 100%;
        z-index: 10;
        height: 0;
        overflow: hidden;
        background: #E0E0E0;
        transition: all 0.5s;
    }
    .aside .foodlist li{
        float: none;
        width: 95%;    
    }
    /*打開foodlist*/
    .open .foodlist{
       height: auto;
    }
    .open .openlist .line1{
        transform: translate(9.5px,9px) rotate(420deg);
    }
    .open .openlist .line2{
        transform: translate(-7px,-3px) rotate(-420deg);
    }
    .open .openlist .line3{
        transform: translate(1px,-0.5px) rotate(360deg);
    }
}
@media(max-width:320px){
    .section .topbar input{
        width: 85%;
    }
    .section .topbar ul{
        margin: 10px;
    }
}
.order_list .order_pic{
    position: relative;
}
.order_pic i{
    font-size: 20px;
    position: absolute;
    top: 0;
    left: 0;
    color: #fff;
    background: #000;
    padding: 5px;
}
.order_pic img{
    max-width: 100%;
}
/*產品清單右上的緞帶設計*/
.order_pic .ribbon_box{
    position: absolute;
    top:-20px;
    right: -10px;
    width: 100px;
    height: 100px;
    margin: 5px;
    text-align: center;
    overflow: hidden;
}
.order_pic .ribbon{
    position: relative;
    top:20px;
    right:2px;
    width: 120%;
    text-align: center;
    padding: 5px;
    z-index: 9;
    background: #69F0AE;
    transform: rotate(45deg);
}
/*產品欄文字設計*/
.order_list h3{
    padding: 10px;
}
.order_list p{
    font-size: 12px;
}
.order_list .order_name{
    font-size: 20px;
}
.order_list hr{
    width: 80px;
}
.order_list .price{
    font-size: 20px;
    margin: 20px;
}
/*加入購物車按紐*/
.order_list .order_btn{
    color: #F56C23;
    border: 1px solid #F56C23;
    width: 90%;
    margin: 0 auto;
    background: none;
    padding: 10px 5px;
    cursor: pointer;
}
.order_list .order_btn:hover{
    color: #00E676;
    border-color:#00E676; 
}
